import "../styles/home.css";
import Header from "./Header";
import Footer from "./Footer";
const Home = (props) => {
  const handleCarousel = (ev) => {
    const gap0 = 16;

    const carousel0 = document.getElementById("carousel0"),
      content0 = document.getElementById("content0"),
      next0 = document.getElementById("next0"),
      prev0 = document.getElementById("prev0");

    next0.addEventListener("click", (e) => {
      carousel0.scrollBy(width0 + gap0, 0);
      if (carousel0.scrollWidth !== 0) {
        prev0.style.display = "flex";
      }
      if (
        content0.scrollWidth - width0 - gap0 <=
        carousel0.scrollLeft + width0
      ) {
        next0.style.display = "none";
      }
    });
    prev0.addEventListener("click", (e) => {
      carousel0.scrollBy(-(width0 + gap0), 0);
      if (carousel0.scrollLeft - width0 - gap0 <= 0) {
        prev0.style.display = "none";
      }
      if (
        !content0.scrollWidth - width0 - gap0 <=
        carousel0.scrollLeft + width0
      ) {
        next0.style.display = "flex";
      }
    });

    let width0 = carousel0.offsetWidth;
    window.addEventListener("resize", (e) => (width0 = carousel0.offsetWidth));
  };

  //Generos de todos los titulos
  const genders = props.movies.map((movie) => {
    return movie.genero;
  });

  // Filtramos losgeneros para obtener el genero no repetido

  const arrayGender = genders.sort().filter((gender, index) => {
    return genders.indexOf(gender) === index;
  });

  //Renderizar los titulos por genero

  //1. Necesitamos la imagen, que a su vez necesitara el gender, que renderiza renderShows

  const renderTitle = (arrayGender, gender) => {
    return props.movies.map((show) => {
      if (show.genero === gender)
        return <img class="item" src={show.imagen} alt="portada pelicula" />;
    });
  };

  //2. Renderizamos los titulos en funcion del genero, y renderizamos el titulo por cada genero llamando a renderTitle

  const renderShows = (arrayGender) => {
    console.log(arrayGender);

    return arrayGender.map((gender) => {
      return (
        <>
          <p class="text-white ms-5 mt-5">{gender}</p>

          <div id="wrapper">
            <div class="carousel" id="carousel0">
              <div class="content" id="content0">
                {renderTitle(arrayGender, gender)}
              </div>
            </div>
            <button class="prev" id="prev0" onClick={handleCarousel}>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="24"
                height="24"
                viewBox="0 0 24 24"
              >
                <path fill="none" d="M0 0h24v24H0V0z" />
                <path d="M15.61 7.41L14.2 6l-6 6 6 6 1.41-1.41L11.03 12l4.58-4.59z" />
              </svg>
            </button>
            <button class="next" id="next0" onClick={handleCarousel}>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="24"
                height="24"
                viewBox="0 0 24 24"
              >
                <path fill="none" d="M0 0h24v24H0V0z" />
                <path d="M10.02 6L8.61 7.41 13.19 12l-4.58 4.59L10.02 18l6-6-6-6z" />
              </svg>
            </button>
          </div>
        </>
      );
    });
  };

  return (
    <>
      <Header />
      <body>
        <main>{renderShows(arrayGender)}</main>
        <Footer />
      </body>
    </>
  );
};

export default Home;
